    @Override
    public void viewTypeCallback(int view_type) {

    }

    private class HomeAdapter extends RecyclerView.Adapter<HomeAdapter.ViewHolder>{

        List<ItemModel> arrayList = new ArrayList<>();
        Context context;

        public HomeAdapter(List<ItemModel> arrayList, Context context) {
            this.arrayList = arrayList;
            this.context = context;
        }

        @NonNull
        @Override
        public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
            View view1 = LayoutInflater.from(parent.getContext()).inflate(R.layout.row_item_category, parent,false);
            return new ViewHolder(view1);
        }

        @Override
        public void onBindViewHolder(@NonNull ViewHolder holder, int position) {

            ItemModel model = arrayList.get(position);
            holder.tv_name.setText(model.getName());
            Glide.with(context)
                    .load(arrayList.get(position).getUrl())
                    .into(holder.iv_image);

        }

        @Override
        public int getItemCount() {
            return arrayList.size();
        }

        public class ViewHolder extends RecyclerView.ViewHolder {
            ImageView iv_image;
            TextView tv_name;
            LinearLayout ll_main;
            public ViewHolder(View itemView) {
                super(itemView);

                iv_image = itemView.findViewById(R.id.image);
                tv_name = itemView.findViewById(R.id.tv_category_grid);
                ll_main = itemView.findViewById(R.id.ll_main_category);

                ll_main.setOnClickListener(new View.OnClickListener() {
                    @Override
                    public void onClick(View view) {
                        ItemModel itemModel = arrayList.get(getPosition());

                        Intent ii = new Intent(context, SearchActivity.class);
                        ii.putExtra("CATEGORY_ID", itemModel.getId());
                        ii.putExtra("CATEGORY_NAME", itemModel.getName());
                        ii.putExtra("CASE","3");
                        startActivity(ii);
                    }
                });

            }
        }
    }

    private class ItemModel{

        private String id;
        private String name;
        private String url;

        public ItemModel(String id, String name, String url) {
            this.id = id;
            this.name = name;
            this.url = url;
        }

        public String getId() {
            return id;
        }

        public void setId(String id) {
            this.id = id;
        }

        public String getName() {
            return name;
        }

        public void setName(String name) {
            this.name = name;
        }

        public String getUrl() {
            return url;
        }

        public void setUrl(String url) {
            this.url = url;
        }
    }


